INSERT INTO `al_article` (`id`, `cid`, `order`, `title`, `customlink`, `url`, `source`, `author`, `userid`, `pic`, `keywords`, `tags`, `description`, `filename`, `pubdate`, `hits`, `digg`, `comments`, `type`, `visible`, `postype`, `sno`) VALUES
(1, 3, 0, 'Level 1   Wiggle Kids (2.5-3岁)', 'niudongdexiaojingling_2_5_3sui_tishinenxueqian1ji_', '', '', '', 1, 'uploadfiles/201502/2015020314330272992.jpg', '', '', '孩子从幼儿体适能升级到儿童体适能课程，也是从自由发展到进入学习简单规则的重要时期，课程特有的Fit circle中孩子可以进行Figure it out（动动脑）表演的同时发展想象力和语言表达能力，悬吊系统可以让孩子尽情地荡起秋千，在室内有序的感受户外才能享受到的兴奋与快乐。除此之外，太极，瑜伽，特色瑜伽，风格各异的流行简单舞蹈，以及有辅助性的平衡木、翻滚以及单双杠等挑战，孩子的表现会让您和他自己不仅大吃一惊且兴奋不已。', 'fitkid/1413301619', 1413301619, 0, 0, 0, '0', '1', 0, '000'),
(2, 3, 0, 'Level 2   Bounce Kids (3-4岁)', 'linghuodexiaoyundongjia_3_4sui__tishinenxueqian2ji_', '', '', '', 1, 'uploadfiles/201502/2015020314325589694.jpg', '', '', '孩子成为了解简单规则的独立“小大人”，完全自主的参加团体及合作性游戏，特色的瑜伽球鼓环节让他们在舞动的同时发展乐感。升级版的全球各种异域风情的舞蹈，加上热门卡通及音乐元素的加入，让课堂趣味无穷。鱼跃前滚翻，倒立，悬吊等各种体操动作，在指导师的引导下让他们更加独立的完成，锻炼敏捷性和运动感。', 'fitkid/1413301734', 1413301734, 0, 0, 0, '0', '1', 0, '000'),
(3, 3, 0, 'Level 3   Mambo Kids (4-5岁)', 'qingjingmofangxiaozhuanjia_4_5sui_tishinenxueqian3ji_', '', '', '', 1, 'uploadfiles/201502/2015020314323793109.jpg', '', '', '孩子进入情感爆发期，具有很丰富的记忆联想能力，课程融入了更高难度的Sports Skill体操动作训练，同时注重团队合作的Action Training, 让孩子不仅掌握运动的技巧，更能够作为团队的成员进行合作。', 'fitkid/1413301777', 1413301777, 0, 0, 0, '0', '1', 0, '000'),
(4, 3, 0, 'Level 4   Hopper Kids (5-6岁)', 'xiaoxiaoshejiaojia_5_6sui__tishixueqian4ji_', '', '', '', 1, 'uploadfiles/201502/2015020314322027268.jpg', '这个阶段的小朋友跳高，跳舞，律动都已基本处于定型的阶段，情感处于摇摆期，并容易受到外界情感波动的影响，内心世界丰富，善于运用各种途径表达自己。乐于与其他小朋友参与组织性的学习。对老师的动作指导跟随可以复制到50%。', '', '小朋友在这个阶段成长为多样表达者，拥有丰富的内心世界，孩子们可以将之前学习的各种运动技能和技巧在Race Day竞赛日里一较高下，并互相学习，开始实操和模拟球类及田径类的竞赛，让他们在运动和体格的发展上更自信。', 'fitkid/1413304563', 1413304563, 0, 0, 0, '0', '1', 0, '000'),
(6, 4, 0, 'Level 1      Music Cradle  (0-12个月)', 'niudongdexiaojingling_2_5_3sui_tishinenxueqian1ji_', '', '', '', 1, 'uploadfiles/201502/2015020314312930515.jpg', '', '', '随着宝宝好奇心不断上升，他们努力学习爬行的动作，开始寻找音源，模仿父母的发声，享受着乐器发出的美妙声音所带给他们的快乐。通过创意思维的舞蹈，发声游戏，对物体及乐器的探索活动，使他们的所有感官都得到刺激。', 'music/1413305172', 1413305172, 0, 0, 0, '0', '1', 0, '000'),
(7, 4, 0, 'Level 2   Rock with Music  (13-18个月)', 'Rock_with_Musicyaodongdeyinle_13_18_geyue_aileyinleerji_', '', '', '', 1, 'uploadfiles/201502/2015020314311473764.jpg', '', '', '鼓励学步的幼儿建立自信心，自我控制力，增强沟通技巧的同时发掘引人入胜的音乐世界。学唱歌，模仿声音，通过带韵律的歌谣和物体识别训练可以促进语言沟通技能的发展，根据各种音乐所表达的“情绪”变化而产生的创造性的运动可以发展平衡感，时间意识和空间意识。听力练习和轮流等待的活动可以帮助幼儿提升社交技能。', 'music/1413305213', 1413305213, 0, 0, 0, '0', '1', 0, '000'),
(8, 4, 0, 'Level 3   Music Path  (19-26个月)', 'Music_Pathyinlezhilu_19_26geyue_aileyinlesanji_', '', '', '', 1, 'uploadfiles/201502/2015020314310624390.jpg', '', '', '所有的活动都是通过音乐，乐器，诗歌，游戏等多种方式帮助孩子更积极的参与音乐体验。此课程系列将有效的鼓励宝宝发挥创造力，激发他们自己的思维，并用自己的方式做出回应，在演奏乐器、唱歌、参与活动、探索活动的时候，帮助树立宝宝的自信心、自制力和交流能力。', 'music/1413305246', 1413305246, 0, 0, 0, '0', '1', 0, '000'),
(9, 4, 0, 'Level 4   Music Parade  (27-36个月)', 'Music_Around_yinlehuanyou_27_36geyue_aileyinlesiji_', '', '', '', 1, 'uploadfiles/201502/201502031430597541.jpg', '', '', '精选各类学科知识范围的主题，并配合诗、歌、曲、图片、故事、模仿秀和丰富的游戏帮助孩子们吸收到更多知识性的营养。孩子们将更加熟练地使用各种打击乐器来表现音乐的稳定节拍和不同的节奏型。他们开始听懂音乐中的情景与感受，自主选择适当的乐器进行演奏配音。孩子们在一个毫无压力的环境中，通过学习音乐概念、音乐语言、音乐符号以及了解著名的作曲家来打下扎实的音乐基础。', 'music/1413305278', 1413305278, 0, 0, 0, '0', '1', 0, '000'),
(10, 4, 0, 'Level 5   Music All Around    (3-5岁)', 'Music_All_Aroundyinletuantuanzhuan_3_5sui_aileyinlewuji_', '', '', '', 1, 'uploadfiles/201502/2015020314305151874.jpg', '', '', '以儿童早期成长和学习特点的研究为基础，围绕音乐设计的语言和早期阅读的课程。课程中的活动利用儿童最喜欢的音乐活动和主题故事，开发3-5岁儿童的早期阅读理解，语言表达以及沟通技能。孩子们将独立上课，更好地与学前课程衔接，为孩子做好入学准备。', 'music/1413305314', 1413305314, 0, 0, 0, '0', '1', 0, '000'),
(11, 5, 0, 'Art 1   Sensory Fun  (1.5-2岁)', 'ganjuedabenying_18_24geyue_yishuchuangzuo_1_ji_', '', '', '', 1, 'uploadfiles/201502/2015020314300597491.jpg', '', '', '丰富的艺术体验是艺术创作1阶段宝宝的一双翅膀，感官大本营课程的开放式环境恰恰提供给宝宝们一个可以自由翱翔的创意空间。为了丰富小宝宝们的多种艺术体验机会，除了选用专业的儿童艺术材料外，一个面团、一张报纸、一碗大米、一个纸盒甚至一盆水，这些安全、适龄的生活常见物品，都可以成为最佳的艺术创作原材料。宝宝们开始以极大的热情投入到这种看得见，摸得着，来自于生活的感觉艺术创作形式中。他们不断地探索物品的质地及特性，不仅可以将手中的橡皮泥拍、捏、扯、戳、压，还可以尝试使用各种小工具了！小手的精细肌肉在大脑的支配下进行多', 'art/1413305611', 1413305611, 0, 0, 0, '0', '1', 0, '000'),
(12, 5, 0, 'Art 2   Art Exploration（2-3岁）', 'tansuoyishuxinjie_24_36geyue_yishuchuangzuo_2_ji_', '', '', '', 1, 'uploadfiles/201502/2015020314295897631.jpg', '', '', '艺术创作2阶段孩子们的想象力及表达能力飞速发展，他们有更强的思考能力及动手能力，手工制作开始依赖平面及立体两种形式，老师们更注重孩子们想象力及创造力的表达过程，而绝不会强调作品本身的好坏把他们引入禁锢的木框中。自由绘画时间，老师鼓励每一位孩子用自己的方式进行涂鸦，帮助培养孩子独立的人格，孩子们可以通过勾勒在画纸上的点、线条或是色块等任意一种符号来表达自己的情绪或想法。让我们一起去看一看孩子如何探索及使用新工具，如何试验并混合出新颜色，去听一听他们如何表达认知到的世界或是想象中的那些人和事儿吧！', 'art/1413305644', 1413305644, 0, 0, 0, '0', '1', 0, '000'),
(13, 5, 0, 'Art 3   Little Artist（3-5岁）', 'woshixiaoyishujia_3_5sui_yishuchuangzuo3_ji_', '', '', '', 1, 'uploadfiles/201502/2015020314294722153.jpg', '', '', '小艺术家们将开始独立上课，老师引导孩子们围绕每周的主题展开想象并进行创作，他们可以更自主的选择艺术探索的方式方法，而不受成人影响。他们将使用不同的艺术材料来创作他们的“大师级”作品。在这个年龄段，指导师还会更加关注孩子们的社交能力，学习新词汇，帮助他们表达自我，引导他们进行问题的解决，意愿表达，情绪控制，物品分享及协调合作等。', 'art/1413305686', 1413305686, 0, 0, 0, '0', '1', 0, '000'),
(14, 6, 0, 'Level 1   Kitchen Helper（3-4.5岁）', 'chufangxiaobangshou_3sui_4_5sui_jiankangchuqu1ji_', '', '', '', 1, 'uploadfiles/201502/2015020314291590289.jpg', '', '', '孩子充分探索真实食材的质地及特性，了解世界上不同国家的食品种类并娴熟掌握多样美食制作工具的使用方法，整个动手制作过程中食材的色香味变化让所有的感官系统完全发挥作用，使孩子们参与及探索的兴趣大大提升，合作制作环节让孩子们自主交流，学会协作、尊重、分享，建立更加独立自信的人格。', 'cookingfun/1413305973', 1413305973, 0, 0, 0, '0', '1', 0, '000'),
(15, 6, 0, 'Level 2   Little Cook （4.5-6岁）', 'chushixiaonenshou_4_5sui_6sui_jiankangchuqu2ji_', '', '', '', 1, 'uploadfiles/201502/2015020314290715821.jpg', '', '', '孩子独立参与全程美食制作活动，学习必要的餐桌礼仪, 提升自控力和组织纪律性， 大孩子对食物种类划分已经有了明确的归类概念，形成了较有针对性的烹饪设计意识和独特的制作方法， 能够准确地判断各种辅助原材料的量比将材料自如进行运用，以提高食品的制作工艺品质，这一系列与生活紧密相连的体验经历将帮助孩子们建立健康的营养根基和良好饮食习惯。', 'cookingfun/1413306026', 1413306026, 0, 0, 0, '0', '1', 0, '000'),
(31, 23, 0, '什么是早教', 'shimeshizaojiao', '', '', '', 1, '', '', '', '1：0-6的教育影响孩子的一生\r\n2：父母是孩子的第一任老师\r\n3：早教的核心在于家庭教育\r\n从大脑发展的脑神经学来说，刚出生的孩子处于脑神经快速发展阶段，通过接触周围事物刺激神经元形成连结\r\n。在这个过程，孩子处于一个敏感的时期，如果我们能够给到对的，充分的刺激，那么他的大脑发育就比较完\r\n善。\r\n早教不仅仅是教孩子，也是在教家长。如果父母没有给到一些正确的引导，那么孩子的发展方向很容易发生错\r\n误。当孩子长', 'jiameng/1415689581', 1415689581, 0, 0, 0, '0', '1', 0, ''),
(16, 7, 0, 'Level 1   Letter Wizard（3-4岁）', 'zimuxiaojingling__3_4sui_feifanyingyu1ji_', '', '', '', 1, 'uploadfiles/201502/2015020314283694256.jpg', '', '', '初级课程适用于尚未掌握26个英文字母音素的孩子。课程运用直接和间接的教学方法，包括互动游戏、智能互动白板、编排生动并极具关联性的图画、歌谣、故事，激发儿童学习积极性，根据英语发音的出现频率，遵循全新的字母发音学习顺序，教授孩子英文26个字母的发音，字母名称，字形，书写笔顺及简单的词汇组成，让孩子们了解字母中元音与辅音的区别，初步认知字母的组合形成单词的规律。在学习初期为英语学习打下夯实的基础。', 'letterland/1413306255', 1413306255, 0, 0, 0, '0', '1', 0, '000'),
(20, 27, 0, 'Level 1 （0-6个月）', 'Level_1_0_6geyue_', '', '', '', 1, 'uploadfiles/201502/2015020314273141211.jpg', '', '', '小宝宝正处于对世界的观察期，课程依据适龄性设置了丰富的感观游戏，专注于宝宝的视觉、听觉、触觉、体能、平衡全发展的同时为新生婴儿营建出安全舒适的社交环境，让父母与宝宝建立更紧密的亲子关系。', 'fitbaby/1414312767', 1414312767, 0, 0, 0, '0', '1', 0, '000'),
(17, 7, 0, 'Level 2   Words Juggler（4-5岁）', 'zicizashuashi_4_5sui_feifanyingyu2ji_', '', '', '', 1, 'uploadfiles/201502/2015020314282079318.jpg', '', '', '中级课程融入复习巩固第一阶段中的发音及字母书写方法，并加入教授基于这些发音规则基础上的双字母发音及新单词的拼写组合。组词游戏、字母人物情景剧等多感官学习策略促进儿童全面发展，本阶段的学习将带领孩子们进入音位的深入学习，介绍二合字母和三合字母，学习掌握更多高频词汇，让英文学习更加系统化。', 'letterland/1413306289', 1413306289, 0, 0, 0, '0', '1', 0, '000'),
(18, 7, 0, 'Level 3   Smart Reader（5-6岁）', 'yueduxiaonenshou_5_6sui_feifanyingyu3ji_', '', '', '', 1, 'uploadfiles/201502/2015020314281296347.jpg', '', '', '高级课程适合于已掌握英文字母基本音素和主要的二合字母和三合字母发音能力及基本拼读能力的孩子。课程将介绍更多重要字母组合，为全面提升读写能力打下坚实的基础。课程将介绍16个新音位（语音单位）。教学依然会以生动有趣的故事来引入新的发音和拼写规则，孩子们就如交新朋友一样快乐，复杂的拼写规则变得如此轻松易懂。', 'letterland/1413306320', 1413306320, 0, 0, 0, '0', '1', 0, '000'),
(21, 27, 0, 'Level 2 (6-10个月)', 'Level_2_6_10geyue_', '', '', '', 1, 'uploadfiles/201502/2015020314250055676.jpg', '', '', '宝宝对世界充满好奇并且开始探索世界，无论是对于已爬行还是准备爬行的宝贝都可以通过设备游戏增强肌肉力量，提升平衡及协调能力，为更好的运动打下坚实的基础，多样的主题内容让宝宝们保持重复的基础上掌握并运用更多技巧来认知和了解世界。', 'fitbaby/1414312813', 1414312813, 0, 0, 0, '0', '1', 0, '000'),
(22, 27, 0, 'Level 3  (10-16个月)', 'Level_3_10_16geyue_', '', '', '', 1, 'uploadfiles/201502/201502031424532533.jpg', '', '', '宝宝可以从扶持站立到慢慢学会独立行走，游戏内容及运动范围的扩大提供给这个阶段宝宝更广的社交空间，运动的同时宝宝们会通过丰富的面部表情，手势，眼神和声音来进行表达尝试沟通，课程中父母会成为宝宝最亲密的交流者。', 'fitbaby/1414312874', 1414312874, 0, 0, 0, '0', '1', 0, '000'),
(23, 27, 0, 'Level 4  (16-22个月)', 'Level_4_16_22geyue_', '', '', '', 1, 'uploadfiles/201502/2015020314244495901.jpg', '', '', '宝宝行动能力及独立解决问题能力逐步提升，可以在设备游戏中独立自如地衡量空间、距离、高度，提升运动计划性，同时每周课程内容紧密围绕“概念”主题开展，将运动、认知、语言融为一体，促进宝宝大脑发育。', 'fitbaby/1414312934', 1414312934, 0, 0, 0, '0', '1', 0, '000'),
(24, 27, 0, 'Level 5  (22-28个月)', 'Level_5_22_28geyue_', '', '', '', 1, 'uploadfiles/201502/2015020314243425788.jpg', '', '', '宝宝开始发展想象力和创造能力，这个阶段的课程紧密围绕想象性主题进行开展，猴子荡秋千，小熊爬山，火车钻山洞等游戏让宝宝与家人同时融入新的角色，在趣味的运动游戏中宝宝们用语言和符号表达自己的创意思维。', 'fitbaby/1414312969', 1414312969, 0, 0, 0, '0', '1', 0, '000'),
(25, 27, 0, 'Level 6（28-36个月)', 'Level_6_28_36geyue_', '', '', '', 1, 'uploadfiles/201502/2015020314242593710.jpg', '', '', '宝宝处于顺序和逻辑关系建立的黄金时期，他们了解故事中的逻辑，在体能发育时间中运用复杂的单边或双边动作及思维技巧进行假扮游戏，课程将一系列有逻辑的想象游戏融入到运动环节中，让孩子们用专属的儿童思维方式进行探索。', 'fitbaby/1414313029', 1414313029, 0, 0, 0, '0', '1', 0, '000'),
(26, 8, 0, '公主主题', 'gongzhuzhuti', '', '', '', 1, '', '', '', '朦胧的月色,如蝉翼般一倾而泄，犹如午夜的阳光刺破黑暗，给神秘的森林披上了一席银纱。让我们穿上水晶鞋，乘着南瓜马车，踏过七彩之光，去赴一场只属于公主们的华丽舞会吧。\r这里有敢于在迷雾中寻找宝箱的茉莉儿公主，勇于奉献的美人鱼公主，聪明善良的贝儿公主，还有梦幻的舞台、绚丽的宝石、古老的神灯、&hellip;..，和更多意想不到的惊喜等着您宝宝来发现。\r带着您的宝宝来一次城堡中的仙旅奇缘吧，让宝宝用她自己', 'party/1414403994', 1414403994, 0, 0, 0, '0', '1', 0, ''),
(27, 8, 0, '骑士主题', 'qishizhuti', '', '', '', 1, '', '', '', '孩童时的你，是否也曾幻想过自己是这样的：“身披华服，手持长剑，是一位代表着正义与勇气的斗龙勇士”。\r\n如今的你已为人父母，站在时光的路口，回望儿时的美好，却终究抵不过现实。\r\n而此时，您的宝宝也拥有和您曾经无比相近的梦。\r\n勇气需要传承，而遗憾无需重演。我们不仅可以实现您宝宝的“勇士梦”也弥补您曾遗失的美好。\r\n我们将带您和您的宝宝乘着时光机，穿越重重时空。', 'party/1414404066', 1414404066, 0, 0, 0, '0', '1', 0, ''),
(28, 8, 0, '印第安主题', 'yindianzhuti', '', '', '', 1, '', '', '', '月光将圣洁撒向大地，当雄鹰傲然搏击长空，空灵的奥卡里与山谷回荡齐鸣，让我们一起穿梭时空，回到那片辽阔的北美大地，穿过苍莽的热带雨林，来一次神秘的印第安探险。\r\n带着鹰羽冠的酋长，奇特的无拱顶房屋、趣味的农作物种植、可以捕捉梦境的神秘之网和令人热血沸腾的弓箭狩猎等，更多的秘密等着您和宝宝来一起开启！                           \r\n带着宝贝一起踏上奇妙的印第安之旅，感受那别样的异', 'party/1414404200', 1414404200, 0, 0, 0, '0', '1', 0, ''),
